  //     cookies.remove('start')
  //     window.location.reload()
  // }

  // else if (screen === 2) setTaskScreen()
  // else setLoginScreen()
  console.log(screen, setLoginScreen, setTaskScreen)

}

const LoginBar = (screen, setTaskScreen, setLoginScreen) => {
    return (
      <div className='login-bar'>
          <img className='login-img' src={login} alt='login' />
          {signBtn()}
          <div className='log' onClick={ () => handleLoginBtn(screen, setTaskScreen, setLoginScreen)}>{txt()}</div>
      </div>
    )
  }

  export default LoginBar
