import React, {useState} from 'react'
import Cookies from 'universal-cookie'
import './App.sass'
import TaskAdd from './TaskAdd'
import TaskList from './TaskList'
import LoginBar from './LoginBar'
import Login from './Login'
import LoginFirst from './LoginFirst'
import Register from './Register'
import StartMobile from './StartMobile'
import ForgotPass from './ForgotPass'
import Footer from './Footer'

const cookies = new Cookies()

const App = () => {
  const [screen, setScreen] = useState(0)
  // 0 - StartMobile, 1 - Register, 2 - Login, 3 - LoginFirst, 4 - Task, 5 - ForgotPass

  const setRegisterScreen = () => {
    setScreen(1)
  }

  const setLoginScreen = () => {
    setScreen(2)
  }

  const setTaskScreen = () => {
    setScreen(4)
  }

  const setForgotScreen = () => {
    setScreen(5)
  }

  return (
    <div className='container'>
      { screen !== 0 && <LoginBar screen={screen} setTaskScreen={setTaskScreen} setLoginScreen={setLoginScreen}/> }
      { screen === 0 && <StartMobile setTaskScreen={setTaskScreen} setLoginScreen={setLoginScreen} setRegisterScreen={setRegisterScreen}/> }
      { screen === 1 && <Register /> }
      { screen === 2 && <Login setRegisterScreen={setRegisterScreen} setForgotScreen={setForgotScreen} /> }
      { screen === 3 && <LoginFirst /> }
      { screen === 4 && <TaskAdd /> }
      { screen === 4 && <TaskList /> }
      { screen === 5 && <ForgotPass /> }
      <Footer />
    </div>
  )
}

export default App
