import React, {useState} from 'react'
import './App.sass'
import TaskAdd from './TaskAdd'
import TaskList from './TaskList'
import LoginBar from './LoginBar'
import Login from './Login'
import LoginFirst from './LoginFirst'
import Register from './Register'
import StartMobile from './StartMobile'
import ForgotPass from './ForgotPass'
import Footer from './Footer'

const App = () => {
  const [screen, setScreen] = useState(1)
  // 0 - StartMobile, 1 - Register, 2 - Login, 3 - LoginFirst, 4 - Task, 5 - ForgotPass

  const setTaskScreen = () => {
    console.log('task screen')
    setScreen(4)
  }

  const setLoginScreen = () => {
    console.log('login screen')
    setScreen(2)
  }

  return (
    <div className='container'>
      { screen!==0 && <LoginBar screen={screen} setTaskScreen={setTaskScreen} setLoginScreen={setLoginScreen}/>}
      { screen==0 && <StartMobile />}
      { screen==1 && <Register />}
      { screen==2 && <Login />}
      { screen==3 && <LoginFirst />}
      { screen==4 && <TaskAdd /> && <TaskList />}
      { screen==5 && <ForgotPass />}
      <Footer />
    </div>
  )
}

export default App
